function App() {
  const containerRef = useRef();
  const [currentIndex, setCurrentIndex] = useState(0);
  const isScrollingRef = useRef(false);
  const location = useLocation(); // useLocation을 통해 location 객체를 가져옵니다.

  const navigateTo = (index) => {
    setCurrentIndex(index);
  };

  const handleScroll = (event) => {
    event.preventDefault();
    if (!isScrollingRef.current) {
      const direction = event.deltaY > 0 ? 1 : -1;
      let nextIndex = currentIndex + direction;
      nextIndex = Math.max(0, Math.min(nextIndex, 5)); // 0부터 5까지의 섹션
      setCurrentIndex(nextIndex);
      isScrollingRef.current = true;
      setTimeout(() => {
        isScrollingRef.current = false;
      }, 150);
    }
  };

  useEffect(() => {
    containerRef.current.style.transform = `translateY(-${currentIndex * 100}vh)`;
    const headerBox = document.querySelector('.header--box');
    if (currentIndex > 0) {
      headerBox.classList.add('active');
    } else {
      headerBox.classList.remove('active');
    }
    document.querySelectorAll('.box--item').forEach((item, index) => {
      item.classList.remove('active');
      if (index === currentIndex) {
        item.classList.add('active');
      }
    });
  }, [currentIndex]);

  useEffect(() => {
    const container = containerRef.current;
    container.addEventListener('wheel', handleScroll);
    return () => {
      container.removeEventListener('wheel', handleScroll);
    };
  }, [handleScroll]);

  // 페이지가 새로고침될 때 맨 위로 스크롤
  useEffect(() => {
    window.scrollTo(0, 0);
    setCurrentIndex(0);
  }, [location.pathname]); // location.pathname이 변경될 때마다 실행

  return (
    <div className="main">
      <Header currentIndex={currentIndex} navigateTo={navigateTo} />
      <div className="body--box" ref={containerRef} style={{ transformStyle: 'preserve-3d', transition: 'transform 0.7s', overflowY: 'scroll' }}>
      <Router>
        <Routes>
          <Route path="/" element={<Section1 />} />
          {/* <Route path="/section2" element={<Section2 />} />
          <Route path="/section3" element={<Section3 />} />
          <Route path="/section4" element={<Section4 />} />
          <Route path="/section5" element={<Section5 />} />
          <Route path="/section6" element={<Section6 />} /> */}
        </Routes>
      </Router>
        {/* <Section1 /> */}
        {/* <Section2 />
        <Section3 />
        <Section4 />
        <Section5 />
        <Section6 /> */}
      </div>
      <Footer />
    </div>
  );
}

export default App;
