import "../styles/Main.scss";
import { useState, useRef, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

import creativeImg from '../assets/images/3d-view-puzzle-pieces.png';
import globalmg from '../assets/images/globalmg.png';
import planetImg from '../assets/images/planetImg.png';
import rochetImg from '../assets/images/rochetImg.png';
import footerLogo from '../assets/images/footerLogo.png';
import H_logo from '../assets/images/white_logo.png';
import b_logo from '../assets/images/black_logo.png';

// import { GoogleMap, LoadScript } from '@react-google-maps/api';
import Map from "../components/Map";


function App() {

const [activeSection, setActiveSection] = useState(null); // 초기 상태를 null로 설정

const handleScroll = () => {
  const sections = document.querySelectorAll('.box--item');
  let closestSectionIndex = null;
  let minDistance = Infinity;

  sections.forEach((section, index) => {
    const sectionTop = section.offsetTop - window.scrollY;
    const distance = Math.abs(sectionTop);
    if (distance < minDistance) {
      minDistance = distance;
      closestSectionIndex = index;
    }
  });

  // closestSectionIndex가 null이 아니고, 현재 activeSection과 다를 경우에만 업데이트
  if (closestSectionIndex !== null && closestSectionIndex !== activeSection) {
    // 모든 섹션에서 'active' 클래스 제거
    sections.forEach((section) => section.classList.remove('active'));

    // 가장 가까운 섹션에 'active' 클래스 추가
    sections[closestSectionIndex].classList.add('active');

    // 상태 업데이트
    setActiveSection(closestSectionIndex);

    // header--box에 'active' 클래스 조건부 추가
    const headerShouldBeActive = closestSectionIndex !== 0;
    document.querySelector('.header--box').classList.toggle('active', headerShouldBeActive);
  }
};



  const scrollToSection = (sectionIndex) => {
    const sections = document.querySelectorAll('.box--item');
    if (sections[sectionIndex]) {
      window.scrollTo({
        top: sections[sectionIndex].offsetTop,
        behavior: 'smooth' // 부드러운 스크롤 효과
      });
    }
  };


  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);





  const [currentSection, setCurrentSection] = useState(0);
  const sectionsRef = useRef([]);

  useEffect(() => {
    sectionsRef.current = sectionsRef.current.slice(0, 6); // 6개 섹션에 대한 참조를 저장
  }, []);

  const handleWheel = (e) => {
    e.preventDefault(); // 기본 스크롤 동작 방지
    if (e.deltaY > 0) { // 아래로 스크롤
      if (currentSection < 5) { // 마지막 섹션보다 앞에 있을 경우
        setCurrentSection(currentSection + 1);
      }
    } else { // 위로 스크롤
      if (currentSection > 0) { // 첫 번째 섹션보다 뒤에 있을 경우
        setCurrentSection(currentSection - 1);
      }
    }
  };

  useEffect(() => {
    const section = sectionsRef.current[currentSection];
    if (section) {
      window.scrollTo({
        top: section.offsetTop,
        behavior: "smooth",
      });
    }
  }, [currentSection]);

  useEffect(() => {
    window.addEventListener("wheel", handleWheel, { passive: false });
    return () => window.removeEventListener("wheel", handleWheel);
  }, [handleWheel]);

  return (
    <div className="main">
      <div className={`header--box`}>
        <div className="logo"
          onClick={() => scrollToSection(0)}
        ></div>
        <ul>
        {['VISION', 'MISSION', 'CREATIVE', 'GLOBAL', 'GOAL', 'CONTACT'].map((item, index) => (
          <li key={index} className={activeSection === index ? 'active' : ''}
          onClick={() => scrollToSection(index)}
          >
            {item}
          </li>
        ))}
        </ul>
