import React, { useEffect, useRef, useState } from "react";
import "../styles/Main.scss";
// 다른 import들...

function App() {
  const containerRef = useRef();
  const headerRef = useRef();
  const itemsRef = useRef([]); // box--item들에 대한 참조를 배열로 관리
  const [currentIndex, setCurrentIndex] = useState(0); // useState를 사용하여 currentIndex를 관리
  let isScrolling = false;

  useEffect(() => {
    itemsRef.current = itemsRef.current.slice(0, 3); // item 개수에 맞게 조정
  }, []);

  const updateActiveClass = () => {
    // 모든 items에서 active 클래스 제거
    itemsRef.current.forEach((item, index) => {
      if (item) {
        item.classList.remove("active");
      }
    });

    // 현재 item에 active 클래스 추가
    const currentItem = itemsRef.current[currentIndex];
    if (currentItem) {
      currentItem.classList.add("active");
    }

    // header--box의 active 클래스 조정
    if (currentIndex > 0) {
      headerRef.current.classList.add("active");
    } else {
      headerRef.current.classList.remove("active");
    }
  };

  const scrollHandler = (event) => {
    if (isScrolling) return;
    isScrolling = true;

    const direction = event.deltaY;
    let nextIndex = currentIndex + (direction > 0 ? 1 : -1);
    nextIndex = Math.max(0, Math.min(nextIndex, itemsRef.current.length - 1)); // 범위 내로 제한

    if (nextIndex !== currentIndex) {
      setCurrentIndex(nextIndex); // currentIndex 업데이트
      updateActiveClass(); // active 클래스 업데이트
    }

    // 스크롤 애니메이션 후 isScrolling 리셋
    setTimeout(() => {
      isScrolling = false;
    }, 1000);
  };

  useEffect(() => {
    const container = containerRef.current;
    container.addEventListener("wheel", scrollHandler);

    return () => {
      container.removeEventListener("wheel", scrollHandler);
    };
  }, [currentIndex]); // currentIndex가 변경될 때마다 useEffect 실행

  return (
    <div className="main">
      <div className="header--box" ref={headerRef}>
        {/* 헤더 콘텐츠 */}
      </div>
      <div className="body--box" ref={containerRef}>
        <div ref={(el) => (itemsRef.current[0] = el)} className="box--item item1">
          {/* item1 콘텐츠 */}
        </div>
        <div ref={(el) => (itemsRef.current[1] = el)} className="box--item item2">
          {/* item2 콘텐츠 */}
        </div>
        <div ref={(el) => (itemsRef.current[2] = el)} className="box--item item3">
          {/* item3 콘텐츠 */}
        </div>
      </div>
    </div>
  );
}

export default App;
