import "../styles/Main.scss";
import { useState, useRef, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

import creativeImg from '../assets/images/3d-view-puzzle-pieces.png';
import globalmg from '../assets/images/globalmg.png';
import H_logo from '../assets/images/white_logo.png';
import b_logo from '../assets/images/black_logo.png';

function App() {
  const headerRef = useRef(); // header--box 참조
  const navItemRefs = useRef([]); // nav items에 대한 참조를 저장할 배열
  const [currentIndex, setCurrentIndex] = useState(0); // 현재 활성화된 item의 인덱스
  const isScrollingRef = useRef(false); // 스크롤 중인지 확인하는 플래그
  const itemRefs = useRef([useRef(), useRef(), useRef(), useRef()]); // 각 box--item에 대한 참조를 저장할 배열

  // 스크롤 이벤트 핸들러, 이벤트 리스너에서 제거
  // const handleScroll = (event) => {...};

  useEffect(() => {
    // 스크롤 이벤트 리스너 제거
    // const container = containerRef.current;
    // container.addEventListener('wheel', handleScroll);
    // return () => {
    //   container.removeEventListener('wheel', handleScroll);
    // };
  }, []); // handleScroll 의존성 제거

  // 각 nav item 클릭 시 실행할 함수
  const navigateTo = (index) => {
    setCurrentIndex(index);
    if (itemRefs.current[index] && itemRefs.current[index].current) {
      const top = itemRefs.current[index].current.offsetTop;
      window.scrollTo({
        top: top,
        behavior: "smooth",
      });
    }
  };

  useEffect(() => {
    // currentIndex 변경에 따른 로직은 유지
    // 페이지 스크롤 이동 로직 제거
    navItemRefs.current.forEach((ref, index) => {
      if (ref) {
        ref.classList.remove("active");
      }
    });

    if (navItemRefs.current[currentIndex]) {
      navItemRefs.current[currentIndex].classList.add("active");
    }

    const headerBox = headerRef.current;
    if (currentIndex > 0) {
      headerBox.classList.add('active');
    } else {
      headerBox.classList.remove('active');
    }
  }, [currentIndex]);

  return (
    <div className="main">
      <div className="header--box" ref={headerRef}>
        <div className="logo" onClick={() => navigateTo(0)}><img src={H_logo} alt="Logo" /></div>
        <ul>
          <li onClick={() => navigateTo(0)}>VISION</li>
          <li onClick={() => navigateTo(1)}>MISSION</li>
          <li onClick={() => navigateTo(2)}>CREATIVE</li>
          <li onClick={() => navigateTo(3)}>GLOBAL</li>
          {/* CONTACT 항목 제거됨 */}
        </ul>
      </div>
      <div className="body--box">
        <div ref={itemRefs.current[0]} className="box--item item1">
          <div className="title"><span>No.1</span> IDIOTS MAKE THE BEST GAMES</div>
          <div className="sub--title"><span>NIGAMES</span> is a game development startup founded in 2024.</div>
          <div className="sub--text">
            NIGAMES is a developer and publisher specializing in WEB2 and WEB3 games, with unique ideas, it continues to challenge the global game market and new technologies. We strive to make games for users of all tastes.
          </div>
        </div>
        <div ref={itemRefs.current[1]} className="box--item item2">
          <div className="gnb--title">MISSION</div>
          <div className="title">OUR GOOD DAYS BECOME A BETTER FUTURE</div>
          <div className="sub--text">
            We strive to create games for users of all ages, platforms, and tastes. We never think about the past. For the future Yes! We are fools who work together to do so.
          </div>
        </div>
        <div ref={itemRefs.current[2]} className="box--item item3">
          <div className="box--item--content--left"><img src={creativeImg} alt="CREATIVE_IMG"/></div>
          <div className="box--item--content--right">
            <div className="title">CREATIVE</div>
            <div className="sub--text">
              Creative ideas, originality, non-stick challenging spirit, NIGAMES is about creating a future full of laughter and happiness with everyone in the world.
            </div>
          </div>
        </div>
        <div ref={itemRefs.current[3]} className="box--item item4">
          <div className="box--item--content--left">
            <div className="title">GLOBAL</div>
            <div className="sub--text">
              NIGAMES, which seeks the best, develops and delivers competitive games. It will bring joy and happiness to all users around the world. We can always break down our own thoughts and flip them over if it's a better way.
            </div>
          </div>
          <div className="box--item--content--right"><img src={globalmg} alt="global_IMG"/></div>
        </div>
      </div>
    </div>
  );
}

export default App;
